mapper.select:
select *
from tb_product_selling sel inner join tb_product prod on sel.code = prod.code
where
1=1

AND code = #{code}


AND company_id = #{companyId}

select tb_product.*, tb_product.inventory_amount as amount, tb_product.selled_amount as selledAmount, tb_product.image_url as imageUrl, tb_product.thumbnail_url as thumbnailUrl, tb_company.companyName as companyName from tb_product inner join tb_company on tb_product.company_id = tb_company.companyId where 1=1
AND code = #{code}
AND company_id = #{companyId}
