mapper.select:
select * from tb_product_selling sel inner join tb_product prod on sel.code = prod.code where 1=1
AND sel.code = #{code}
AND sel.company_id = #{companyId}
AND (sel.start_timestamp <= (select UNIX_TIMESTAMP()) AND sel.end_timestamp >= (select UNIX_TIMESTAMP()))

select tb_product.*, tb_product.inventory_amount as inventoryAmount, tb_product.selled_amount as selledAmount, tb_product.image_url as imageUrl, tb_product.thumbnail_url as thumbnailUrl, tb_company.companyName as companyName from tb_product inner join tb_company on tb_product.company_id = tb_company.companyId where 1=1
AND code = #{code}
AND company_id = #{companyId}

select acode code1, bcode code2, ccode code3, name from tb_product_ccode
WHERE
  acode = #{code1}
  AND
  bcode = #{code2}

select acode code1, bcode code2, ccode code3, dcode code4, name from tb_product_dcode
 WHERE
  acode = #{code1}
  AND
  bcode = #{code2}
  AND
  ccode = #{code3}
