mapper.select:
select pur.id, pur.product_id productId, pur.product_code productCode, tb_product.name productName, tb_product.info productInfo, tb_product.spec productSpec, pur.customer_id customerId, pur.register_date registerDate, pur.delivery_date deliveryDate, pur.arrival_date arrivalDate, pur.keeping_date keepingDate, pur.delivery_id deliveryId, pur.delivery_flag deliveryFlag, pur.price, pur.count FROM tb_product_purchase pur inner join tb_product on pur.product_code = tb_product.code WHERE 1=1
AND product_code = #{productCode}
AND customer_id = #{userId}
AND tb_product.company_id = #{companyId}
